"""
栈的操作
Stack() 创建一个新的空栈
push(item) 添加一个新的元素item到栈顶
pop() 弹出栈顶元素
peek() 返回栈顶元素
is_empty() 判断栈是否为空
size() 返回栈的元素个数
"""
class Stack(object):
    """栈类"""
    def __init__(self):
        self.stack = []

    def push(self,item):
        """添加一个新的元素item到栈顶"""
        self.stack.append(item)

    def pop(self):
        """弹出栈顶元素"""
        return self.stack.pop()

    def peek(self):
        """返回栈顶元素"""
        return self.stack[-1]

    def is_empty(self):
        """判断栈是否为空"""
        return len(self.stack) == 0

    def size(self):
        """返回栈的元素个数"""
        return len(self.stack)


if __name__ == '__main__':
    s = Stack()
    print(s.is_empty())
    print(s.size())
    s.push(1)
    print(s.is_empty())
    print(s.size())
    for i in range(5):
        s.push(i)
    print(s.stack)
    print(s.peek())
    print("*"*20)
    while True:
        try:
            print(s.pop())
        except IndexError:
            break

